const axios = require('axios');
function Login() {
  const [post, setpost] = useState();
  const state={apiResponse: ""};  
  const {
      register,
      handleSubmit,
      formState: { errors },
    } = useForm();

    const onSubmit = (data) => {
      const resultat={
        login:data.login,
        password:data.password,
      };
      axios.post(`http://localhost:4242/login`,resultat)
        .then(res => {
          console.log(res);
          console.log(res.data);// resultat de node login welcome blabla
          this.setstate({apiResponse: res.data})
        })

    };

return (
  <React.Fragment>
  <form onSubmit={handleSubmit(onSubmit)}>
  login
  <input {...register('login')} /> {/* register an input */}
  password<input {...register('password', { required: true })} />
  <input type="submit" />
</form>
  <p>{state.apiResponse}</p>
</React.Fragment>

);

}

export default Login;
