import React, {Fragment, useState} from 'react';
import "../index.css"
import { useForm } from 'react-hook-form';


const axios = require('axios');

function Inscription() {
        const [state, setpost] = useState();
        const {
            register,
            handleSubmit,
            formState: { errors },
          } = useForm();
          const onSubmit = (data) => {
            const resultat={
              login:data.login,
              email:data.email,
              password:data.password,
              passwordConfirm:data.passwordConfirm,
            };
            axios.post(`http://localhost:4242/register`,resultat)
              .then(res => {
                console.log(res);
                //console.log(res.data);
                setpost(res.data)
              })

          };

      return (
        <React.Fragment>
        <form onSubmit={handleSubmit(onSubmit)}>
        login
        <input {...register('login')} /> {/* register an input */}
        email<input {...register('email', { required: true })} />
        password<input {...register('password', { required: true })} />
        passwordConfirm<input {...register('passwordConfirm', { required: true })} />
        <input type="submit" />
      </form>
      <p>{state}</p>
      </React.Fragment>
      );
  }

  export default Inscription;
